function alertSuccess(t,a){$.notify({title:t,message:a},{style:"a_success",autoHide:!0,clickToHide:!0,position:"bottom right",autoHideDelay:3e3})}function alertError(t,a){$.notify({title:t,message:a},{style:"a_error",autoHide:!1,clickToHide:!1,position:"bottom right"})}$.notify.addStyle("a_success",{html:"<div><div class='alert alert-success alert-dismissible alert-right'><button type='button' class='close' data-dismiss='alert'>×</button><h4><i class='icon fa fa-check'></i><span data-notify-text='title'/></h4><span data-notify-html='message'/></div></div>"}),$.notify.addStyle("a_error",{html:"<div><div class='alert alert-danger alert-dismissible alert-right'><button type='button' class='close' data-dismiss='alert'>×</button><h4><i class='icon fa fa-warning'></i><span data-notify-text='title'/></h4><span data-notify-html='message'/></div></div>"});